    private void cmbPegawaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmbPegawaiActionPerformed
        if (cmbPegawai.getSelectedItem() == null) return;

        String data = cmbPegawai.getSelectedItem().toString();
        String[] split = data.split(" - ");
        txtGajiPokok.setText(split[2]);
    }//GEN-LAST:event_cmbPegawaiActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int gajiPokok = Integer.parseInt(txtGajiPokok.getText());
        int lembur = txtLembur.getText().isEmpty() ? 0 : Integer.parseInt(txtLembur.getText());
        int potongan = txtPotongan.getText().isEmpty() ? 0 : Integer.parseInt(txtPotongan.getText());

        int total = gajiPokok + lembur - potongan;
        txtTotalGaji.setText(String.valueOf(total));
    }//GEN-LAST:event_jButton1ActionPerformed

    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        try {
        if (txtTanggal.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Tanggal belum diisi!");
            return;
        }

        if (txtTotalGaji.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Klik Hitung terlebih dahulu!");
            return;
        }

        String data = cmbPegawai.getSelectedItem().toString();
        int idPegawai = Integer.parseInt(data.split(" - ")[0]);

        java.sql.Date sqlDate = java.sql.Date.valueOf(txtTanggal.getText());

        int lembur = txtLembur.getText().isEmpty() ? 0 : Integer.parseInt(txtLembur.getText());
        int potongan = txtPotongan.getText().isEmpty() ? 0 : Integer.parseInt(txtPotongan.getText());

        Connection c = DatabaseConnection.getConnection();
        String sql = "INSERT INTO gaji (id_pegawai, tanggal, lembur, potongan, total_gaji) VALUES (?,?,?,?,?)";
        PreparedStatement p = c.prepareStatement(sql);
        p.setInt(1, idPegawai);
        p.setDate(2, sqlDate);
        p.setInt(3, lembur);
        p.setInt(4, potongan);
        p.setInt(5, Integer.parseInt(txtTotalGaji.getText()));
        p.executeUpdate();

        JOptionPane.showMessageDialog(this, "Gaji berhasil disimpan");
        tampilData();
        btnResetActionPerformed(null);

    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, "Format tanggal harus yyyy-MM-dd");
    }
    }//GEN-LAST:event_btnSimpanActionPerformed

