    private void loadPegawai() {
        try {
            Connection c = DatabaseConnection.getConnection();
            Statement s = c.createStatement();
            ResultSet r = s.executeQuery(
                "SELECT pegawai.id_pegawai, pegawai.nama, jabatan.gaji_pokok " +
                "FROM pegawai JOIN jabatan ON pegawai.id_jabatan = jabatan.id_jabatan"
            );

            cmbPegawai.removeAllItems();
            while (r.next()) {
                cmbPegawai.addItem(
                    r.getInt("id_pegawai") + " - " +
                    r.getString("nama") + " - " +
                    r.getInt("gaji_pokok")
                );
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
}

    private void tampilData() {
        DefaultTableModel model = new DefaultTableModel();
        model.addColumn("ID");
        model.addColumn("Nama Pegawai");
        model.addColumn("Tanggal");
        model.addColumn("Total Gaji");

        try {
            Connection c = DatabaseConnection.getConnection();
            Statement s = c.createStatement();
            ResultSet r = s.executeQuery(
                "SELECT gaji.id_gaji, pegawai.nama, gaji.tanggal, gaji.total_gaji " +
                "FROM gaji JOIN pegawai ON gaji.id_pegawai = pegawai.id_pegawai"
            );

            while (r.next()) {
                model.addRow(new Object[]{
                    r.getInt(1),
                    r.getString(2),
                    r.getDate(3),
                    r.getInt(4)
                });
            }
            tblGaji.setModel(model);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
}

