    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed

    try {
        Connection c = DatabaseConnection.getConnection();
        String sql = "INSERT INTO jabatan (nama_jabatan, gaji_pokok) VALUES (?,?)";
        PreparedStatement p = c.prepareStatement(sql);
        p.setString(1, txtNamaJabatan.getText());
        p.setInt(2, Integer.parseInt(txtGajiPokok.getText()));
        p.executeUpdate();

        JOptionPane.showMessageDialog(this, "Data berhasil disimpan");
        tampilData();
        resetForm();
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage());
    }


    }//GEN-LAST:event_btnSimpanActionPerformed

    private void tblJabatanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblJabatanMouseClicked
        int row = tblJabatan.getSelectedRow();
        txtNamaJabatan.setText(tblJabatan.getValueAt(row, 1).toString());
        txtGajiPokok.setText(tblJabatan.getValueAt(row, 2).toString());
    }//GEN-LAST:event_tblJabatanMouseClicked

    private void btnUbahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUbahActionPerformed
        int row = tblJabatan.getSelectedRow();
    if (row == -1) {
        JOptionPane.showMessageDialog(this, "Pilih data terlebih dahulu!");
        return;
    }

    String id = tblJabatan.getValueAt(row, 0).toString();

    try {
        Connection c = DatabaseConnection.getConnection();
        String sql = "UPDATE jabatan SET nama_jabatan=?, gaji_pokok=? WHERE id_jabatan=?";
        PreparedStatement p = c.prepareStatement(sql);
        p.setString(1, txtNamaJabatan.getText());
        p.setInt(2, Integer.parseInt(txtGajiPokok.getText()));
        p.setString(3, id);
        p.executeUpdate();

        JOptionPane.showMessageDialog(this, "Data berhasil diubah");
        tampilData();
        resetForm();
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage());
    }

    }//GEN-LAST:event_btnUbahActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
int row = tblJabatan.getSelectedRow();
    if (row == -1) {
        JOptionPane.showMessageDialog(this, "Pilih data terlebih dahulu!");
        return;
    }

    String id = tblJabatan.getValueAt(row, 0).toString();

    try {
        Connection c = DatabaseConnection.getConnection();
        String sql = "DELETE FROM jabatan WHERE id_jabatan=?";
        PreparedStatement p = c.prepareStatement(sql);
        p.setString(1, id);
        p.executeUpdate();

        JOptionPane.showMessageDialog(this, "Data berhasil dihapus");
        tampilData();
        resetForm();
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage());
    }
    }//GEN-LAST:event_btnHapusActionPerformed

