    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        try {
            String jabatan = cmbJabatan.getSelectedItem().toString();
            int idJabatan = Integer.parseInt(jabatan.split(" - ")[0]);

            Connection c = DatabaseConnection.getConnection();
            String sql = "INSERT INTO pegawai (nama, alamat, no_hp, id_jabatan) VALUES (?,?,?,?)";
            PreparedStatement p = c.prepareStatement(sql);
            p.setString(1, txtNama.getText());
            p.setString(2, txtAlamat.getText());
            p.setString(3, txtNoHp.getText());
            p.setInt(4, idJabatan);
            p.executeUpdate();

            JOptionPane.showMessageDialog(this, "Data pegawai berhasil disimpan");
            tampilData();
            resetForm();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
    }//GEN-LAST:event_btnSimpanActionPerformed

    private void tblPegawaiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblPegawaiMouseClicked
        int row = tblPegawai.getSelectedRow();
        txtNama.setText(tblPegawai.getValueAt(row, 1).toString());
        txtAlamat.setText(tblPegawai.getValueAt(row, 2).toString());
        txtNoHp.setText(tblPegawai.getValueAt(row, 3).toString());
    }//GEN-LAST:event_tblPegawaiMouseClicked

    private void btnUbahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUbahActionPerformed
        int row = tblPegawai.getSelectedRow();
    if (row == -1) {
        JOptionPane.showMessageDialog(this, "Pilih data terlebih dahulu!");
        return;
    }

    String id = tblPegawai.getValueAt(row, 0).toString();
    String jabatan = cmbJabatan.getSelectedItem().toString();
    int idJabatan = Integer.parseInt(jabatan.split(" - ")[0]);

    try {
        Connection c = DatabaseConnection.getConnection();
        String sql = "UPDATE pegawai SET nama=?, alamat=?, no_hp=?, id_jabatan=? WHERE id_pegawai=?";
        PreparedStatement p = c.prepareStatement(sql);
        p.setString(1, txtNama.getText());
        p.setString(2, txtAlamat.getText());
        p.setString(3, txtNoHp.getText());
        p.setInt(4, idJabatan);
        p.setString(5, id);
        p.executeUpdate();

        JOptionPane.showMessageDialog(this, "Data berhasil diubah");
        tampilData();
        resetForm();
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage());
    }
    }//GEN-LAST:event_btnUbahActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
        int row = tblPegawai.getSelectedRow();
    if (row == -1) {
        JOptionPane.showMessageDialog(this, "Pilih data terlebih dahulu!");
        return;
    }

    String id = tblPegawai.getValueAt(row, 0).toString();

    try {
        Connection c = DatabaseConnection.getConnection();
        String sql = "DELETE FROM pegawai WHERE id_pegawai=?";
        PreparedStatement p = c.prepareStatement(sql);
        p.setString(1, id);
        p.executeUpdate();

        JOptionPane.showMessageDialog(this, "Data berhasil dihapus");
        tampilData();
        resetForm();
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage());
    }
    }//GEN-LAST:event_btnHapusActionPerformed

