    private void resetForm() {
    txtNama.setText("");
    txtAlamat.setText("");
    txtNoHp.setText("");
    cmbJabatan.setSelectedIndex(0);
    tblPegawai.clearSelection();
}

    private void loadJabatan() {
    try {
        Connection c = DatabaseConnection.getConnection();
        Statement s = c.createStatement();
        ResultSet r = s.executeQuery("SELECT * FROM jabatan");

        cmbJabatan.removeAllItems();

        while (r.next()) {
            cmbJabatan.addItem(
                r.getInt("id_jabatan") + " - " + r.getString("nama_jabatan")
            );
        }
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage());
    }
}

private void tampilData() {
    DefaultTableModel model = new DefaultTableModel();
    model.addColumn("ID");
    model.addColumn("Nama");
    model.addColumn("Alamat");
    model.addColumn("No HP");
    model.addColumn("Jabatan");

    try {
        Connection c = DatabaseConnection.getConnection();
        Statement s = c.createStatement();
        ResultSet r = s.executeQuery(
            "SELECT pegawai.id_pegawai, pegawai.nama, pegawai.alamat, pegawai.no_hp, jabatan.nama_jabatan " +
            "FROM pegawai JOIN jabatan ON pegawai.id_jabatan = jabatan.id_jabatan"
        );

        while (r.next()) {
            model.addRow(new Object[]{
                r.getInt("id_pegawai"),
                r.getString("nama"),
                r.getString("alamat"),
                r.getString("no_hp"),
                r.getString("nama_jabatan")
            });
        }
        tblPegawai.setModel(model);
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage());
    }
}

